import React, { Component } from 'react'
import PropTypes from 'prop-types';


export class TodoItem extends Component {

    getButtonStyle = () => {
        return {
            backgroud: '#ff0000',
            color: '#fff',
            border: 'none',
            padding: '5px 9px',
            borderRadius: '50%',
            cursor: 'pointer',
            float: 'right'
        }
    }

    getStyle = () => {
        return {
            backgroud: '#f4f4f4',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            textDecoration: this.props.todo.completed ?
                'line-through' : 'none'
        }
    }

    render() {
        const { id, title } = this.props.todo;
        return (
            <div style={this.getStyle()}>
                <p>
                    <input type="checkbox" onChange={
                        this.props.markCompleted.bind(this, id)} /> {' '}
                    {title}
                    <button onClick={this.props.delTodo.bind(this, id)} style={this.getButtonStyle()}>x</button>
                </p>
            </div>
        )
    }
}

// Prop types
TodoItem.PropType = {
    todo: PropTypes.object.isRequired
}

export default TodoItem
